import React, { useEffect, useState } from 'react'
import Gallery from 'react-photo-gallery'
import axios from 'axios'

const getPhotos = async () => axios.get('http://host.docker.internal/api/v1/images')

function GalleryPhotos () {
  const [images, SetImages] = useState(false)

  useEffect(() => {
    if (!images) {
      const urls = []
      getPhotos().then(({ data }) => {
        for (const image of data) {
          urls.push(image.url)
        }
        SetImages(urls)
      })
    }
  }, [images])

  const handlePhotos = () => {
    const response = []
    for (const image of images) {
      response.push({ src: image, width: 4, height: 3 })
    }
    return response
  }

  return (
    <>
      <svg
        height='100%'
        width='100%'
        id='svg'
        viewBox='0 0 1440 400'
        xmlns='http://www.w3.org/2000/svg'
        class='transition duration-300 ease-in-out delay-150'
      >
        <path
          d='M 0,400 C 0,400 0,200 0,200 C 114.35714285714289,156.53571428571428 228.71428571428578,113.07142857142858 351,131 C 473.2857142857142,148.92857142857142 603.4999999999998,228.25 713,248 C 822.5000000000002,267.75 911.2857142857144,227.92857142857142 1029,210 C 1146.7142857142856,192.07142857142858 1293.3571428571427,196.03571428571428 1440,200 C 1440,200 1440,400 1440,400 Z'
          stroke='none'
          stroke-width='0'
          fill='#151418ff'
          class='transition-all duration-300 ease-in-out delay-150'
          transform='rotate(-180 720 200)'
        />
      </svg>
      <div className='ProjectWrapper' id='gallery'>
        <div className='Container'>
          <div className='SectionTitle'>As Fotos ❤️ </div>
          {images ? <Gallery photos={handlePhotos()} /> : ''}

        </div>
      </div>
    </>
  )
}

export default GalleryPhotos
